def mobstats(mob):
    if (mob == "weed"):
        creatureHealth = weedHealth
        creatureDamage = weedDamage
        creatureAttackEXP = weedAttackExp
        creatureDefenceEXP = weedDefenceExp
        drops = weedDrops
        dropsPercentage = weedDropPercentages
        dropsQuant = weedDropsQuant
    elif (mob == "crab"):
        creatureHealth = crabHealth
